import React from 'react';
import './make-counter.scss';
import Counter from '../counter/counter';

interface Steps {
    step: number;
    stepValue: number;
}

interface MakeCounterModel {
    steps: Steps[];
    message: string;
    activeMessage: string;
    currentStep: number;
    stepValue: number;
}

class MakeCounter extends React.Component{
    makeCounterModel: MakeCounterModel;
    state: {steps: Steps[], activeMessage: string};
    constructor(
            props: Steps
        ) {
        super(props);
        this.state = {
            steps: [],
            activeMessage: ''
        };
        this.makeCounterModel = {
            steps: [],
            message: 'This only accepts numbers like 1, 2, -5, 3.8, 310',
            activeMessage: '',
            currentStep: 1,
            stepValue: 0
        }
    };
    checkUserInput(event: string) {
        const validate: RegExp = /^[+-]?\d+(\.\d+)?$/;
        if (validate.exec(event)) {
            this.setState({activeMessage: ''});
            this.makeCounterModel.stepValue = Number(event);
            return;
        } else {
            this.setState({activeMessage: 'make-counter-parent__message--active'});
            return;
        }
    };

    makeCounter(currentStep: number, stepValue: number) {
        this.makeCounterModel.currentStep++;
        this.makeCounterModel.steps.push({step: currentStep, stepValue: stepValue});
        this.setState({
            steps: this.makeCounterModel.steps
        });
    }

    render() {
        return (
            <>
                <section className="make-counter-parent">
                    <div className="make-counter-parent__counter-maker-holder">
                        <input className="make-counter-parent__steps-input" type="text"
                            placeholder="enter steps value ..."
                            onChange={(e) => this.checkUserInput(e.target.value)} />
                        <button className="make-counter-parent__maker-button" onClick={(e) => this.makeCounter(this.makeCounterModel.currentStep, this.makeCounterModel.stepValue)}>Add counter</button>
                    </div>
                    <div className={`make-counter-parent__message ${this.state.activeMessage}`}>
                        {this.makeCounterModel.message}
                    </div>
                </section>
                <section className="counter-parent">
                    {
                        this.state.steps.map((step) => <Counter step={step.step} stepValue={step.stepValue} defaultStep={0} />)
                    }
                </section>
            </>
        )
    }
}




export default MakeCounter;
