import React from 'react';
import './counter.scss';

class Counter extends React.Component {
    constructor(props: {index: number, step: number}) {
        super(props);
        this.state = {
            index: props.index,
            step: props.step
        }
    }

    render() {
        return (
            <section className="counter-parent">
                <div className="counter-parent__counter">
                    <div className="counter-parent__counter-header">
                        <span>step 0</span>
                    </div>
                    <div className="counter-parent__count">
                        <h3>0</h3>
                    </div>
                    <div className="counter-parent__run-button-holder">
                        <button className="counter-parent__run-button">Run</button>
                    </div>
                </div>
            </section>
        )
    }
}



export default Counter;
