import React from 'react';
import './App.scss';
import Counter from './counter/counter';

interface Steps {
  index: number;
  step: number;
}

class App extends React.Component {
  steps: Steps[];
  constructor(
      props: {} | Readonly<{}>
    ) {
    super(props);
    this.steps = [
        {
            index: 0,
            step: 0
        },
        {
            index: 1,
            step: 1
        },
    ]
  }
  render() {
    return (
      <div className="App">
        {
          this.steps.map((step) => <Counter index={step.index} step={step.step} />)
        }
      </div>
    );
  }
}

export default App;
