import React from 'react';
import './counter.scss';

interface Steps {
    step: number;
    stepValue: number;
  }
class Counter extends React.Component<Steps> {
    constructor(props: Steps) {
        super(props);
        this.state = {
            step: props.step,
            stepValue: props.step,
            defaultStep: 0
        }
    }

    render() {
        return (
            <div className="counter">
                <div className="counter__counter-header">
                    <span>step {this.props.step}</span>
                </div>
                <div className="counter__count">
                    <h3>{this.props.stepValue}</h3>
                </div>
                <div className="counter__run-button-holder">
                    <button className="counter__run-button">Run</button>
                </div>
            </div>
        )
    }
}



export default Counter;
