import React from 'react';
import ReactDOM from 'react-dom';
import './make-counter.scss';

interface Steps {
    index: number;
    step: number;
}

class MakeCounter extends React.Component {
    stepInputValue: number;
    errorMessage: string;
    steps: Steps[];
    constructor(
            props: {} | Readonly<{}>
        ) {
        super(props);
        this.stepInputValue = 0;
        this.errorMessage = 'This only accepts numbers like 1, 2, -5, 3.8, 310';
        this.steps = [
            {
                index: 0,
                step: 0
            },
            {
                index: 1,
                step: 1
            },
        ]
    };
    checkUserInput(event: string) {
        const validate: RegExp = /^[+-]?\d+(\.\d+)?$/;
        if (validate.exec(event)) {
            this.stepInputValue = Number(event);
            return;
        } else {
            console.log(event);
        }
    };

    makeCounter() {
    }

    render() {
        return (
            <>
                <section className="make-counter-parent">
                    <div className="make-counter-parent__counter-maker-holder">
                        <input className="make-counter-parent__steps-input" type="text"
                            placeholder="enter steps value ..."
                            onChange={(e) => this.checkUserInput(e.target.value)} />
                        <button className="make-counter-parent__maker-button" onClick={(e) => this.makeCounter()}>Add counter</button>
                    </div>
                    <div className="make-counter-parent__error-message">
                        {this.errorMessage}
                    </div>
                </section>
                <section className="counter-parent">
                    {this.steps.map((index, step) => {
                        return (
                            <div className="counter-parent__counter">
                                <div className="counter-parent__counter-header">
                                    <span>step {index}</span>
                                </div>
                                <div className="counter-parent__count">
                                    <h3>{step}</h3>
                                </div>
                                <div className="counter-parent__run-button-holder">
                                    <button className="counter-parent__run-button">Run</button>
                                </div>
                            </div>
                        );
                    })}
                </section>
            </>
        )
    }
}




export default MakeCounter;
