import React from 'react';
import './counter.scss';

interface Steps {
    step: number;
    stepValue: number;
    defaultStep: number
  }
class Counter extends React.Component<Steps> {
    state: Steps
    constructor(props: Steps) {
        super(props);
        this.state = {
            step: props.step,
            stepValue: props.stepValue,
            defaultStep: props.defaultStep
        }
    }

    makeCount(stepValue: number) {
        console.log(stepValue);
        this.setState({defaultStep: stepValue})
        setInterval(e => {
            this.setState({defaultStep: stepValue + stepValue})
        }, 1000);
    }

    render() {
        return (
            <div className="counter">
                <div className="counter__counter-header">
                    <span>step {this.state.step}</span>
                </div>
                <div className="counter__count">
                    <h3>{this.state.defaultStep}</h3>
                </div>
                <div className="counter__run-button-holder">
                    <button className="counter__run-button" onClick={(e) => this.makeCount(this.state.stepValue)}>Run</button>
                </div>
            </div>
        )
    }
}



export default Counter;
