import React from 'react';
import './make-counter.scss';
import Counter from '../counter/counter';

interface Steps {
    step: number;
    stepValue: number;
}

class MakeCounter extends React.Component{
    steps: Steps[];
    message: string;
    activeMessage: string;
    currentStep: number;
    stepValue: number;
    state: Steps;
    constructor(
            props: Steps
        ) {
        super(props);
        this.state = {
            step: 0,
            stepValue: 0
        }
        this.steps = []
        this.message = 'This only accepts numbers like 1, 2, -5, 3.8, 310';
        this.activeMessage = '';
        this.currentStep = 1;
        this.stepValue = 0;
    };
    checkUserInput(event: string) {
        const validate: RegExp = /^[+-]?\d+(\.\d+)?$/;
        if (validate.exec(event)) {
            this.stepValue = Number(event);
            return;
        } else {
            this.activeMessage = 'make-counter-parent__message--active';
            return;
        }
    };

    makeCounter(currentStep: number, stepValue: number) {
        this.steps.push({step: currentStep, stepValue: stepValue});
        this.setState(this.steps);
    }

    render() {
        return (
            <>
                <section className="make-counter-parent">
                    <div className="make-counter-parent__counter-maker-holder">
                        <input className="make-counter-parent__steps-input" type="text"
                            placeholder="enter steps value ..."
                            onChange={(e) => this.checkUserInput(e.target.value)} />
                        <button className="make-counter-parent__maker-button" onClick={(e) => this.makeCounter(this.currentStep, this.stepValue)}>Add counter</button>
                    </div>
                    <div className={`make-counter-parent__message ${this.activeMessage}`}>
                        {this.message}
                    </div>
                </section>
                <section className="counter-parent">
                    {this.steps.map((step) => <Counter step={this.state.step} stepValue={step.stepValue} />)}
                </section>
            </>
        )
    }
}




export default MakeCounter;
